import React, { useState } from 'react';
import { Divider, Steps, Button, Space, Card, Checkbox, Col, Form, Input } from 'antd';
import ListWapper from "../common/ListWapper";
import AppRowContainer from '../../@crema/core/AppRowContainer';
import { useIntl } from 'react-intl';
import { AiFillEdit } from "react-icons/ai";
import { useSelector, useDispatch } from 'react-redux'
import { useNavigate } from "react-router-dom";
import { getProfile } from '../../store/AuthRedux'
import IntlMessages from '../../@crema/utility/IntlMessages';




const Registration = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch()
  const { messages } = useIntl()

  const [current, setCurrent] = useState(0);
  const onChange = (value) => {
    console.log('onChange:', current);
    setCurrent(value);
  };

  const onFinish = (values) => {
    values['loginType'] = "email"
    dispatch(adminLogin(values))
  };
  const description = 'This is a description.';

  return (
    <ListWapper>
      <div className='participation'>
        <h3>REGISTRATION</h3>
        <h3> Welcome to our easy 3 step registration!</h3>

        {/* <Steps
          current={current}
          onChange={onChange}
          items={[
            {
              title: 'Step 1',
              description,
            },
            {
              title: 'Step 2',
              description,
            },
            {
              title: 'Step 3',
              description,
            },
          ]}
        />

        <Divider />

        <Steps
          current={current}
          onChange={onChange}
          direction="vertical"
          items={[
            {
              title: 'Step 1',
              description,
            },
            {
              title: 'Step 2',
              description,
            },
            {
              title: 'Step 3',
              description,
            },
          ]}
        /> */}

        <Card className='login-card login-card-lg'>
          <AppRowContainer>
            <Col xs={24} lg={24}>
              <Form
                className='login-form'
                name='basic'
                initialValues={{ remember: true }}
                onFinish={onFinish}
              >
                <Form.Item
                  name='name'
                  className='form-field'
                  rules={[
                    { required: true, message: 'Please input your Name!' },
                  ]}>
                  <Input placeholder={messages['common.name']} />
                </Form.Item>



                <Button
                  type='primary'
                  className='login-form-btn'
                  htmlType='submit'>
                  <IntlMessages id='common.signup' />
                </Button>
              </Form>

            </Col>
          </AppRowContainer>
        </Card>


        <h3>Are you currently teaching ?</h3>

        <Space className='button-sec'>
          <Button type="primary">Yes</Button>
          <Button>No</Button>

        </Space>

      </div>



    </ListWapper>
  );
};


export default Registration;
