import React, { useState } from 'react';
import { Divider, Steps, Button, Space, Card, Checkbox, Col, Form, Input, DatePicker, Radio, InputNumber, Select, Modal } from 'antd';
import ListWapper from "../common/ListWapper";
import AppRowContainer from '../../@crema/core/AppRowContainer';
import { useIntl } from 'react-intl';
import { AiFillEdit } from "react-icons/ai";
import { useSelector, useDispatch } from 'react-redux'
import { useNavigate } from "react-router-dom";
import { getProfile } from '../../store/AuthRedux'
import IntlMessages from '../../@crema/utility/IntlMessages';
const { Step } = Steps;

const Registration = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch()
  const { messages } = useIntl()

  const [current, setCurrent] = useState(0);
  const onChange = (value) => {
    console.log('onChange:', current);
    setCurrent(value);
  };

  const onFinish = (values) => {
    values['loginType'] = "email"
    dispatch(adminLogin(values))
  };
  const description = 'This is a description.';
  const [value, setValue] = useState(1);
  const onChanges = () => {
    console.log('radio checked', e.target.value);
    setValue(e.target.value);
  };

  const [isModal, setIsModal] = useState(false);
  const [isModal1, setIsModal1] = useState(false);







  return (
    <ListWapper>
      <div className='participation'>
        {/* 1t */}
        <h3>REGISTRATION</h3>
        <h3> Welcome to our easy 3 step registration!</h3>

        <Steps current={0}
          onChange={onChange}
        >
          <Step title="aaaa" description={'Step 1'} />
          <Step title="bbb" description={'Step 2'} subTitle="Left 00:00:08" />
          <Step title="ccc" description={'Step 3'} />
        </Steps>



        {/* <Steps
          current={current}
          onChange={onChange}
          items={[
            {
              title: 'Step 1',
              description,
            },
            {
              title: 'Step 2',
              description,
            },
            {
              title: 'Step 3',
              description,
            },
          ]}
        />

        <Divider />

        <Steps
          current={current}
          onChange={onChange}
          direction="vertical"
          items={[
            {
              title: 'Step 1',
              description,
            },
            {
              title: 'Step 2',
              description,
            },
            {
              title: 'Step 3',
              description,
            },
          ]}
        /> */}

        <Card className='login-card login-card-lg'>
          <AppRowContainer>
            <Col xs={24} lg={24}>
              <div className='form-sec'>
                <Form
                  className='login-form'
                  name='basic'
                  initialValues={{ remember: true }}
                  onFinish={onFinish}
